import { Mask } from '../hooks/useMask'


/**
 * 
 * @param value 
 * @param mask
 */
function fitInputValueIntoMask(value: string, mask: Mask): Mask {
    const valueCharacters = value.split('')
    return mask.reduce((result, patternOrCharacter, characterPosition) => {
        if (patternOrCharacter instanceof RegExp) {

        }

        const currentCharacter = value.charAt(characterPosition)

        if (patternOrCharacter instanceof RegExp && patternOrCharacter.test(currentCharacter)) {
            result[characterPosition] = currentCharacter
        }

        return result
    }, [...mask])
}

export default fitInputValueIntoMask
