import { Mask } from '../hooks/useMask'


/**
 * 
 * @param value 
 * @param mask
 */
function fitInputValueIntoMask(value: string, mask: Mask): Mask {
    const valueCharacters = value.split('')

    return mask.reduce((result, patternOrCharacter, characterPosition) => {
        if (characterPosition <= valueCharacters.length - 1) {
            const currentValueCharacter = valueCharacters[characterPosition]

            if (patternOrCharacter instanceof RegExp && patternOrCharacter.test(currentValueCharacter)) {
                result[characterPosition]
            }
        }

        return result
    }, [...mask])
}

export default fitInputValueIntoMask
