import { Mask } from '../hooks/useMask'


/**
 * 
 * @param maskedValue 
 * @param mask
 */
function fitInputValueIntoMask(maskedValue: string, mask: Mask): Mask {
    return mask.reduce((result, patternOrCharacter, characterPosition) => {
        const currentCharacter = maskedValue.charAt(characterPosition)

        if (typeof patternOrCharacter === 'string') {
            result[characterPosition] = patternOrCharacter
        } else if (patternOrCharacter instanceof RegExp) {
            if (patternOrCharacter.test(currentCharacter)) {
                result[characterPosition] = patternOrCharacter
            }
        }

        return result
    }, mask)
}

export default fitInputValueIntoMask
