import React, { useState } from 'react'

import useMask from '../../../src/index'


function Demo({ label, onChange, debug, mask, placeholder, }) {
    const [ value, setValue ] = useState('')

    function handleChange(value: string) {
        onChange?.(value)
        setValue(value)
    }

    const maskProps = useMask({
        value,
        onChange: handleChange,
        mask,
        placeholder,
        debug,
    })

    return (
        <div>
            <label htmlFor="test">
                {label}
            </label>
            <input
                id="test"
                name="test"
                {...maskProps}
            />
        </div>
    );
}

export default Demo
